// ============================================================
// SAVED NOTES â€” persisted in localStorage per week
// ============================================================
const STORAGE_KEY_PREFIX = "pioneer_notes_";

function getSavedNotes(weekLabel) {
  try {
    const raw = localStorage.getItem(STORAGE_KEY_PREFIX + weekLabel);
    return raw ? JSON.parse(raw) : null;
  } catch (e) {
    return null;
  }
}

function saveNotes(weekLabel, data) {
  try {
    localStorage.setItem(STORAGE_KEY_PREFIX + weekLabel, JSON.stringify(data));
  } catch (e) {
    console.warn("Could not save notes:", e);
  }
}

// Gather all note fields on the page and return an object
function collectNotes() {
  const notes = {};
  // Textarea / input fields with data-note-id
  document.querySelectorAll("[data-note-id]").forEach(function (el) {
    notes[el.getAttribute("data-note-id")] = el.value || "";
  });
  // Checkboxes with data-check-id
  document.querySelectorAll("[data-check-id]").forEach(function (el) {
    notes[el.getAttribute("data-check-id")] = el.checked;
  });
  return notes;
}

// Restore saved values into the DOM
function restoreNotes(weekLabel) {
  const saved = getSavedNotes(weekLabel);
  if (!saved) return;
  Object.keys(saved).forEach(function (key) {
    // textarea / input
    var el = document.querySelector('[data-note-id="' + key + '"]');
    if (el) {
      el.value = saved[key];
      return;
    }
    // checkbox
    var cb = document.querySelector('[data-check-id="' + key + '"]');
    if (cb) cb.checked = !!saved[key];
  });
}

// Auto-save on every input / change
function enableAutoSave(weekLabel) {
  document.querySelectorAll("[data-note-id]").forEach(function (el) {
    el.addEventListener("input", function () {
      saveNotes(weekLabel, collectNotes());
    });
  });
  document.querySelectorAll("[data-check-id]").forEach(function (el) {
    el.addEventListener("change", function () {
      saveNotes(weekLabel, collectNotes());
    });
  });
}

// ============================================================
// SHARE functionality
// ============================================================
function shareApp() {
  var shareUrl = window.location.href;
  var shareTitle = "Pioneer Spiritual Growth Tracker";
  var shareText = "Check out this Pioneer Spiritual Growth Tracker for JW meeting preparation!";

  if (navigator.share) {
    navigator.share({
      title: shareTitle,
      text: shareText,
      url: shareUrl
    }).catch(function () {});
  } else {
    // Fallback: copy link to clipboard
    navigator.clipboard.writeText(shareUrl).then(function () {
      alert("Link copied to clipboard! Share it with your friends.");
    }).catch(function () {
      prompt("Copy this link to share:", shareUrl);
    });
  }
}

// ============================================================
// PRINT functionality â€” expand all sections and print
// ============================================================
function printMeetingPrep() {
  // Expand all collapsed/accordion sections before printing
  document.querySelectorAll("details").forEach(function (d) {
    d.setAttribute("open", "");
  });
  // Make all textareas tall enough to show full content
  document.querySelectorAll("textarea").forEach(function (ta) {
    ta.style.height = "auto";
    ta.style.height = ta.scrollHeight + "px";
  });
  window.print();
}

// ============================================================
// WEEK NAVIGATION & SCHEDULE
// ============================================================
let currentWeekIndex = null;

// Helper: parse date string as local time (fixes timezone issues)
function parseLocalDate(dateStr) {
  const [year, month, day] = dateStr.split("-").map(Number);
  return new Date(year, month - 1, day, 0, 0, 0, 0);
}

// Function to get the current week's schedule
function getCurrentWeekSchedule() {
  const today = new Date();
  today.setHours(0, 0, 0, 0);

  if (currentWeekIndex !== null && MEETING_SCHEDULE[currentWeekIndex]) {
    return MEETING_SCHEDULE[currentWeekIndex];
  }

  for (let i = 0; i < MEETING_SCHEDULE.length; i++) {
    const week = MEETING_SCHEDULE[i];
    const start = parseLocalDate(week.startDate);
    const end = parseLocalDate(week.endDate);
    end.setHours(23, 59, 59, 999);
    if (today >= start && today <= end) {
      currentWeekIndex = i;
      return week;
    }
  }

  const lastWeek = MEETING_SCHEDULE[MEETING_SCHEDULE.length - 1];
  const lastEnd = parseLocalDate(lastWeek.endDate);
  if (today > lastEnd) {
    currentWeekIndex = MEETING_SCHEDULE.length - 1;
    return MEETING_SCHEDULE[currentWeekIndex];
  }

  currentWeekIndex = 0;
  return MEETING_SCHEDULE[0];
}

// Navigate to previous or next week
function navigateWeek(direction) {
  // Save current week's notes before navigating
  var currentSchedule = getCurrentWeekSchedule();
  saveNotes(currentSchedule.weekLabel, collectNotes());

  if (currentWeekIndex === null) {
    getCurrentWeekSchedule();
  }

  const newIndex = currentWeekIndex + direction;

  if (newIndex >= 0 && newIndex < MEETING_SCHEDULE.length) {
    currentWeekIndex = newIndex;
    renderCurrentSchedule();
  }
}

// Helper: create a jw.org compliance link
function jwLink(url, text) {
  return '<a href="' + url + '" target="_blank" rel="noopener noreferrer">' + text + '</a>';
}

// Helper: build scripture URL
function scriptureUrl(book, chapter) {
  return "https://www.jw.org/en/library/bible/study-bible/books/" + book + "/" + chapter + "/";
}

// Function to render the schedule on the page
function renderCurrentSchedule() {
  const schedule = getCurrentWeekSchedule();
  const wbUrl = schedule.workbookUrl;
  const wtUrl = schedule.watchtowerUrl;

  // === HEADER SECTION ===
  const weekInfo = document.querySelector(".week-info");
  if (weekInfo) {
    weekInfo.innerHTML = jwLink(wbUrl, "Week of " + schedule.weekLabel);
  }

  const themeElement = document.querySelector(".meeting-theme");
  if (themeElement) {
    themeElement.innerHTML =
      "Midweek Meeting Theme: " +
      jwLink(wbUrl, '<strong>\u201C' + schedule.theme + '\u201D</strong>');
  }

  const scriptureVerse = document.querySelector(".scripture-verse");
  if (scriptureVerse) {
    scriptureVerse.innerHTML = jwLink(wbUrl, schedule.scripture);
  }

  // === BIBLE READING (dynamic) ===
  const bibleReadingEl = document.querySelector(".bible-reading");
  if (bibleReadingEl && schedule.bibleReading) {
    bibleReadingEl.innerHTML =
      "<strong>Bible Reading:</strong> " + jwLink(wbUrl, schedule.bibleReading);
  }

  // === WEEK NAVIGATION BUTTONS ===
  let navButtons = document.querySelector(".week-navigation");
  if (!navButtons) {
    navButtons = document.createElement("div");
    navButtons.className = "week-navigation";
    navButtons.style.cssText =
      "display: flex; gap: 10px; justify-content: center; margin: 15px 0;";

    const prevBtn = document.createElement("button");
    prevBtn.textContent = "\u2190 Previous Week";
    prevBtn.className = "btn-secondary";
    prevBtn.style.cssText =
      "padding: 8px 16px; background: #5B9BD5; color: white; border: none; border-radius: 6px; cursor: pointer; font-size: 0.9rem;";
    prevBtn.onclick = function () {
      navigateWeek(-1);
    };

    const nextBtn = document.createElement("button");
    nextBtn.textContent = "Next Week \u2192";
    nextBtn.className = "btn-secondary";
    nextBtn.style.cssText =
      "padding: 8px 16px; background: #5B9BD5; color: white; border: none; border-radius: 6px; cursor: pointer; font-size: 0.9rem;";
    nextBtn.onclick = function () {
      navigateWeek(1);
    };

    navButtons.appendChild(prevBtn);
    navButtons.appendChild(nextBtn);

    if (weekInfo && weekInfo.parentNode) {
      weekInfo.parentNode.insertBefore(navButtons, weekInfo.nextSibling);
    }
  }

  // Update button states
  const prevBtn = navButtons.querySelector("button:first-child");
  const nextBtn = navButtons.querySelector("button:last-child");
  if (prevBtn) prevBtn.disabled = currentWeekIndex === 0;
  if (nextBtn)
    nextBtn.disabled = currentWeekIndex === MEETING_SCHEDULE.length - 1;

  // === MEETING PREP SECTION ===
  const meetingDiv = document.getElementById("meeting");
  if (meetingDiv) {
    const meetingH2 = meetingDiv.querySelector("h2");
    if (meetingH2) {
      meetingH2.innerHTML =
        "\uD83D\uDCDD " +
        jwLink(wbUrl, "Meeting Preparation \u2014 Week of " + schedule.weekLabel);
    }

    const meetingContent = meetingDiv.querySelector(".meeting-content");
    if (meetingContent) {
      const paragraphs = meetingContent.querySelectorAll("p");
      if (paragraphs.length >= 1) {
        paragraphs[0].innerHTML =
          "<strong>Main Theme:</strong> " +
          jwLink(
            wbUrl,
            '\u201C' + schedule.treasuresTalk + '\u201D / \u201C' + schedule.theme + '\u201D'
          );
      }
    }

    const h3s = meetingDiv.querySelectorAll("h3");
    h3s.forEach(function (h3) {
      if (h3.textContent.includes("Treasures From")) {
        h3.innerHTML = jwLink(wbUrl, "Treasures From God\u2019s Word (10 min)");
      }
      if (h3.textContent.includes("Spiritual Gems")) {
        h3.innerHTML = jwLink(wbUrl, "Spiritual Gems (10 min)");
      }
      if (h3.textContent.includes("Apply Yourself")) {
        h3.innerHTML = jwLink(wbUrl, "Apply Yourself to the Field Ministry");
      }
      if (h3.textContent.includes("Congregation Bible Study")) {
        h3.innerHTML = jwLink(wbUrl, "Congregation Bible Study (30 min)");
      }
    });

    const wtArticleEl = meetingDiv.querySelector(".wt-article-title");
    if (wtArticleEl && schedule.watchtowerArticle) {
      wtArticleEl.innerHTML = jwLink(wtUrl, schedule.watchtowerArticle);
    }

    // === KEY SCRIPTURES FOR WATCHTOWER STUDY ===
    const keyScripturesEl = meetingDiv.querySelector(".key-scriptures-list");
    if (
      keyScripturesEl &&
      schedule.keyScriptures &&
      schedule.keyScriptures.length > 0
    ) {
      var html = '<strong>Key Scriptures:</strong><ul style="margin-top: 8px;">';
      schedule.keyScriptures.forEach(function (s) {
        var url = scriptureUrl(s.book, s.chapter);
        html += "<li>" + jwLink(url, s.ref) + "</li>";
      });
      html += "</ul>";
      keyScripturesEl.innerHTML = html;
    }
  }

  // === STANDALONE ELEMENTS ===
  const treasuresEl = document.querySelector(".treasures-link");
  if (treasuresEl) {
    treasuresEl.innerHTML = jwLink(
      wbUrl,
      'Treasures Talk: \u201C' + schedule.treasuresTalk + '\u201D'
    );
  }

  const workbookLink = document.querySelector(".workbook-link");
  if (workbookLink) {
    workbookLink.href = wbUrl;
  }

  // === SHARE BUTTON (create once) ===
  if (!document.querySelector(".share-btn")) {
    var shareBtn = document.createElement("button");
    shareBtn.className = "share-btn";
    shareBtn.textContent = "\uD83D\uDD17 Share This App";
    shareBtn.style.cssText =
      "display: block; margin: 15px auto; padding: 10px 24px; background: #4CAF50; color: white; border: none; border-radius: 8px; cursor: pointer; font-size: 1rem; font-weight: bold;";
    shareBtn.onclick = shareApp;
    var mainContainer =
      document.querySelector(".meeting-content") ||
      document.querySelector("main") ||
      document.body;
    mainContainer.appendChild(shareBtn);
  }

  // === PRINT BUTTON (create once) ===
  if (!document.querySelector(".print-btn")) {
    var printBtn = document.createElement("button");
    printBtn.className = "print-btn";
    printBtn.textContent = "\uD83D\uDDA8\uFE0F Print Meeting Preparation";
    printBtn.style.cssText =
      "display: block; margin: 10px auto 20px; padding: 10px 24px; background: #2196F3; color: white; border: none; border-radius: 8px; cursor: pointer; font-size: 1rem; font-weight: bold;";
    printBtn.onclick = printMeetingPrep;
    var shareEl = document.querySelector(".share-btn");
    if (shareEl && shareEl.parentNode) {
      shareEl.parentNode.insertBefore(printBtn, shareEl.nextSibling);
    }
  }

  // === RESTORE SAVED NOTES for this week ===
  restoreNotes(schedule.weekLabel);
  enableAutoSave(schedule.weekLabel);

  console.log("Schedule loaded for:", schedule.weekLabel);
}

